import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react';
import { StyleSheet, Text, View, TextInput, TouchableOpacity } from 'react-native';
import { RadioButton } from 'react-native-paper';
import DatePicker from 'react-native-datepicker'

export default function AppForm({ navigation }) {
  const [value, setValue] = React.useState('mp');
  const [date, setDate] = useState(new Date());

  const [produto, setProduto] = useState(''); 
  const [quantidade, setQuantidade] = useState('');
  
  function handleProductChange(produto){ setProduto(produto); } 
  function handleQuantityChange(quantidade){ setQuantidade(quantidade); }
  function handleButtonPress(){ 
    console.log({id: new Date().getTime(), produto, quantidade}); 
    navigation.navigate("AppList");
  }

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Adicionar Produto</Text>
      <View style={styles.inputContainer}> 
        <TextInput 
          style={styles.input}
          onChangeText={handleProductChange} 
          placeholder="Nome do Produto"
          clearButtonMode="always" /> 

        <Text>Tipo do Produto</Text>
        <RadioButton.Group onValueChange={newValue => setValue(newValue)} value={value}>
          <View style={{flexDirection: 'row', alignItems: 'center'}}>
            <RadioButton value="mp" />
            <Text>Matéria Prima</Text>

            <RadioButton value="pa" />
            <Text>Produto Acabado</Text>
          </View>
        </RadioButton.Group>

        <TextInput 
          style={styles.input} 
          placeholder="Valor do Produto"
          clearButtonMode="always" />
        
        <TextInput 
          style={styles.input} 
          onChangeText={handleQuantityChange}  
          placeholder="Digite a quantidade" 
          keyboardType={'numeric'}
          clearButtonMode="always" /> 
        
        <Text>Data de Entrega</Text>
        <DatePicker
          date={date}
          onDateChange={setDate}
        />

        <TouchableOpacity style={styles.button} onPress={handleButtonPress}> 
          <Text style={styles.buttonText}>Salvar</Text> 
        </TouchableOpacity> 
      </View>
      <StatusBar style="light" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#D93600',
    alignItems: 'center',
  },
  title: {
    color: '#fff',
    fontSize: 20,
    fontWeight: 'bold',
    marginTop: 50,
  },
  inputContainer: {
    flex: 1,
    marginTop: 30,
    width: '90%',
    padding: 20,
    borderTopLeftRadius: 10,
    borderTopRightRadius: 10,
    alignItems: 'stretch',
    backgroundColor: '#fff'
  },
  input: {
    marginTop: 10,
    height: 60,
    backgroundColor: '#fff',
    borderRadius: 10,
    paddingHorizontal: 24,
    fontSize: 16,
    alignItems: 'stretch'
  },
  button: {
    marginTop: 10,
    height: 60,
    backgroundColor: 'blue',
    borderRadius: 10,
    paddingHorizontal: 24,
    fontSize: 16,
    alignItems: 'center',
    justifyContent: 'center',
    elevation: 20,
    shadowOpacity: 20,
    shadowColor: '#ccc',
  },
  buttonText: {
    color: '#fff',
    fontWeight: 'bold',
  }
});
