import { StatusBar } from 'expo-status-bar';
import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, View, TextInput, TouchableOpacity } from 'react-native';
import { RadioButton } from 'react-native-paper';
import DatePicker from 'react-native-datepicker';
import axios from 'axios';

export default function AppForm({ route, navigation }) {

  const id = route.params ? route.params.id : undefined;
  const [tipo_produto, setValue] = React.useState('mp');
  const [data_entrega, setDataEntrega] = useState(new Date());
  const [produto, setProduto] = useState(''); 
  const [quantidade, setQuantidade] = useState('');  
  const [valor, setValorProduto] = useState('');

  useEffect(() => {
    if(!route.params) return;
    setValue(route.params.tipo_produto);
    console.log(route.params.produto);
    setDataEntrega(route.params.data_entrega);
    setProduto(route.params.produto);
    setQuantidade(route.params.quantidade);
    setValorProduto(route.params.valor_produto);
  }, [route])


  function handleProductChange(produto){ setProduto(produto); } 
  function handleProductValueChange(valor_produto){ setValorProduto(valor_produto); } 
  function handleQuantityChange(quantidade){ setQuantidade(quantidade); }
  
  function handleButtonPress(){ 
    axios.post('http://192.168.0.19:3001/api/v1/produtos', {produto, tipo_produto, valor, quantidade, data_entrega}).then(response => {
        alert('Cadastro realizado com sucesso!');
        navigation.navigate("AppList");
    });
  }

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Adicionar Produto</Text>
      <View style={styles.inputContainer}> 
        <TextInput 
          style={styles.input}
          onChangeText={handleProductChange} 
          placeholder="Nome do Produto"
          clearButtonMode="always"
          value={produto} /> 

        <Text>Tipo do Produto</Text>
        <RadioButton.Group onValueChange={value => setValue(value)} value={tipo_produto}>
          <RadioButton.Item label="MatÃ©ria Prima" value="mp" color="red" />
          <RadioButton.Item label="Produto Acabado" value="pa" color="red" />
          <RadioButton.Item label="Embalagem" value="emb" color="red" />
        </RadioButton.Group>

        <TextInput 
          style={styles.input} 
          onChangeText={handleProductValueChange} 
          placeholder="Valor do Produto"
          clearButtonMode="always"
          value={valor} />
        
        <TextInput 
          style={styles.input} 
          onChangeText={handleQuantityChange}  
          placeholder="Digite a quantidade" 
          keyboardType={'numeric'}
          clearButtonMode="always"
          value={quantidade} /> 
        
        <Text>Data de Entrega</Text>
        <DatePicker
          date={data_entrega}
          onDateChange={setDataEntrega}
        />

        <TouchableOpacity style={styles.button} onPress={handleButtonPress}> 
          <Text style={styles.buttonText}>Salvar</Text> 
        </TouchableOpacity> 
      </View>
      <StatusBar style="light" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#D93600',
    alignItems: 'center',
  },
  title: {
    color: '#fff',
    fontSize: 20,
    fontWeight: 'bold',
    marginTop: 50,
  },
  inputContainer: {
    flex: 1,
    marginTop: 30,
    width: '90%',
    padding: 20,
    borderTopLeftRadius: 10,
    borderTopRightRadius: 10,
    alignItems: 'stretch',
    backgroundColor: '#fff'
  },
  input: {
    marginTop: 10,
    height: 60,
    backgroundColor: '#fff',
    borderRadius: 10,
    paddingHorizontal: 24,
    fontSize: 16,
    alignItems: 'stretch'
  },
  button: {
    marginTop: 10,
    height: 60,
    backgroundColor: 'blue',
    borderRadius: 10,
    paddingHorizontal: 24,
    fontSize: 16,
    alignItems: 'center',
    justifyContent: 'center',
    elevation: 20,
    shadowOpacity: 20,
    shadowColor: '#ccc',
  },
  buttonText: {
    color: '#fff',
    fontWeight: 'bold',
  }
});
