import React, {useEffect, useState} from 'react';
import {StyleSheet, Text, View, TouchableOpacity} from 'react-native';
 
export default function AppItem(props){
    async function handleEditPress(){ 
        const [item, setItem] = useState([]);
    
        useEffect(() => { 
            axios.get('http://192.168.0.19:3001/api/v1/produtos/' + props.id)
                 .then(response => { 
                     setItems(response.data.map(produto => ({id: produto._id,produto: produto.produto, tipo_produto: produto.tipo_produto, valor: produto.valor, 
                                                              quantidade: produto.quantidade, data_entrega: produto.data_entrega}))); 
                 }); 
            }, [route]);
    
        props.navigation.navigate("AppForm", item);
    }
    
    return (
        <View style={styles.container}>
          <Text style={styles.textItem}>{props.item}</Text>
          <View style={styles.buttonsContainer}>
            <TouchableOpacity style={styles.deleteButton} > 
                <Text style={styles.buttonText}>X</Text> 
            </TouchableOpacity> 
            <TouchableOpacity style={styles.editButton} > 
                <Text style={styles.buttonText} onPress={handleEditPress}>Editar</Text> 
            </TouchableOpacity> 
          </View>
        </View>
      );
}

const styles = StyleSheet.create({
    container: {
      backgroundColor: '#fff',
      marginTop: 20,
      width: '100%'
    },
    buttonsContainer: {
        flexDirection: 'row-reverse',
        alignItems: 'flex-end',
        borderBottomWidth: 1,
        borderBottomColor: '#CCC',
        paddingBottom: 10,
        marginTop: 10,
    },
    editButton: {
        marginLeft: 10,
        height: 40,
        backgroundColor: 'blue',
        borderRadius: 10,
        padding: 10,
        fontSize: 12,
        elevation: 10,
        shadowOpacity: 10,
        shadowColor: '#ccc',
        alignItems: 'center'
    },
    deleteButton: {
        marginLeft: 10,
        height: 40,
        width: 40,
        backgroundColor: 'red',
        borderRadius: 10,
        padding: 10,
        fontSize: 12,
        elevation: 10,
        shadowOpacity: 10,
        shadowColor: '#ccc',
        alignItems: 'center'
    },
    buttonText: {
        color: '#fff',
        fontWeight: 'bold',
    },
    textItem: {
        fontSize: 20,
    }
  });
